var shutUpList = []
var tempArr = []

module.exports = {
    run: async (client, message, prefix) => {
      console.log(shutUpList)

      for (user of shutUpList) {
        if (message.author.id = user){
          mesage.reply("shut up")
        }
      }

      if (message.channel.name == "dark-side" && message.content.startsWith(prefix)) {
        console.log("darkside command recieved")
        var commandArr = message.content.slice(prefix.length).trim().split(" ")
        switch (commandArr[0]) {
          case "shutup":
              var shutUpBool = true
              tempArr = []
              for (user of shutUpList) {
                if (user == getUserFromMention(client, commandArr[1]).id) {
                  shutUpBool = false;
                } else {
                  tempArr.push(user)
                }
              }
              shutUpList = tempArr
              if (shutUpBool) {
                shutUpList.push(getUserFromMention(client, commandArr[1]).id)
              }
            break;
          default:
          console.log("Command " + commandArr[0]) + " not recognized.";

        }
      }
    }
}
